        if (element) {
            if (element.actions != null) {
                for (var i = 0; i < element.actions.length; ++i) {
                    var action = element.actions[i];
                    if (action) {
                        if (action.getTag() == tag) {
                            return action;
                        }
                    }
                }
            }
        }

        return null;
