            var tx = event.pageX;
            var ty = event.pageY;

            var mouseX = (tx - pos.left) / cc.Director.getInstance().getContentScaleFactor();
            var mouseY = (pos.height - (ty - pos.top)) / cc.Director.getInstance().getContentScaleFactor();

