        var z = pos.x + pos.y * this._layerSize.width;

        var tile = this._reusedTileWithRect(rect);

        this._setupTileSprite(tile, pos, gid);

        // get atlas index
        var indexForZ = this._atlasIndexForExistantZ(z);
